import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import Plot from 'react-plotly.js';
import { Link } from 'react-router-dom';

function DrugPriceHistory() {
  const [excelData, setExcelData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchCode, setSearchCode] = useState('');
  const [metaInfo, setMetaInfo] = useState({});
  const [chartData, setChartData] = useState([]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: 'binary' });
      const sheetName = wb.SheetNames[0];
      const ws = wb.Sheets[sheetName];
      const data = XLSX.utils.sheet_to_json(ws);
      setExcelData(data);
    };

    reader.readAsBinaryString(file);
  };

  const formatDate = (yyyymmdd) => {
    if (!yyyymmdd || yyyymmdd.length !== 8) return yyyymmdd;
    return `${yyyymmdd.slice(0, 4)}-${yyyymmdd.slice(4, 6)}-${yyyymmdd.slice(6)}`;
  };

  const handleSearch = () => {
    const data = excelData.filter(
      (row) => row['주성분코드']?.toString().includes(searchCode)
    ).filter(row => row['상한가'] !== 0);

    if (data.length === 0) {
      setFilteredData([]);
      setMetaInfo({});
      setChartData([]);
      return;
    }

    const sortedData = [...data].sort((a, b) => {
      return parseInt(b['적용개시일자']) - parseInt(a['적용개시일자']);
    });

    const uniqueDates = {};
    const deduped = sortedData.filter((item) => {
      const date = item['적용개시일자'];
      if (uniqueDates[date]) return false;
      uniqueDates[date] = true;
      return true;
    });

    const result = [];
    let prevPrice = null;
    let skip = false;

    for (let i = deduped.length - 1; i >= 0; i--) {
      const current = deduped[i];
      const currentPrice = current['상한가'];

      if (prevPrice === currentPrice) continue;
      if (currentPrice === 0) {
        skip = true;
        continue;
      }

      if (skip) {
        skip = false;
        const change = (((currentPrice - prevPrice) / prevPrice) * 100).toFixed(2);
        result.push({
          date: current['적용개시일자'],
          price: currentPrice,
          change: change,
        });
        prevPrice = currentPrice;
        continue;
      }

      const change = prevPrice
        ? (((currentPrice - prevPrice) / prevPrice) * 100).toFixed(2)
        : '';
      result.push({
        date: current['적용개시일자'],
        price: currentPrice,
        change: change,
      });
      prevPrice = currentPrice;
    }

    const finalResult = result.reverse();

    const chartX = finalResult.map((d) => formatDate(d.date));
    const chartY = finalResult.map((d) => d.price);
    const chartTrace = {
      x: chartX,
      y: chartY,
      type: 'scatter',
      mode: 'lines+markers',
      marker: { color: 'blue' },
    };

    const lastRow = sortedData[0];
    const firstRow = sortedData[sortedData.length - 1];
    const row2012 = finalResult.find((row) => row.date === '20120401');

    let explanation = '';
    if (row2012) {
      const pre200701 = finalResult.find((r) => parseInt(r.date) < 20070101);
      const referencePrice = pre200701?.price || firstRow['상한가'];
      const reduced = Math.round(referencePrice * 0.5355);
      explanation = `2007-01-01 기준 약가 ${referencePrice} → 53.55% 적용약가 ${reduced}`;
    }

    setMetaInfo({
      제품코드: lastRow['제품코드'],
      제품명: lastRow['제품명'],
      업체명: lastRow['업체명'],
      주성분코드: searchCode,
      설명: explanation,
    });

    setFilteredData(finalResult);
    setChartData([chartTrace]);
  };

  return (
    <div style={{ padding: '30px' }}>
      <h2><Link to="/" style={{ textDecoration: 'none', color: 'black' }}>💊 MarketAccessNow 홈으로</Link></h2>
      <h3>📌 약가 이력 조회 시스템</h3>
      <p>특정품목의 약가이력을 찾습니다.</p>
      <p>[절차]<br />
        1) 심평원 약가이력 엑셀 다운로드 ▶ 2) 해당파일 업로드 ▶ 3) 엑셀파일내 찾고싶은 품목 주성분코드 입력 ▶ 4) 결과확인
      </p>
      <p>[주요확인 내용]<br />
        1) 복합제 산정시 기준약가<br />
        2) 2012년 4월 1일 일괄인하 여부 (2007년 1월 1일 기준 53.55% 적용 약가인지 여부)<br />
        3) 재평가로 20% 인하 적용여부 (3년에 걸쳐 7%-7%-6% 인하 적용 확인)<br />
        4) 사용량-약가 연동협상으로 인한 약가인하 확인<br />
        5) 실거래가 약가인하 확인<br />
      </p>
      <p>
        [심평원 의약품통합정보 ‘급여 이력정보 검색‘ 링크]<br />
        <a href="https://www.hira.or.kr/ra/medi/getHistoryList.do?pgmid=HIRAA030035020000&WT.gnb=%EC%9D%98%EC%95%BD%ED%92%88%ED%86%B5%ED%95%A9%EC%A0%95%EB%B3%B4"
          target="_blank" rel="noreferrer"
          style={{ color: 'blue', textDecoration: 'underline' }}>
          바로가기
        </a>
      </p>

      <input type="file" onChange={handleFileUpload} /><br /><br />
      <input type="text" value={searchCode} onChange={(e) => setSearchCode(e.target.value)} />
      <button onClick={handleSearch}>조회</button>

      {metaInfo.제품코드 && (
        <div style={{ marginTop: '20px' }}>
          <p>1. 제품코드: {metaInfo.제품코드}</p>
          <p>2. 제품명: {metaInfo.제품명}</p>
          <p>3. 업체명: {metaInfo.업체명}</p>
          <p>4. 주성분코드: {metaInfo.주성분코드}</p>
          <p>5. 급여이력 ⬇⬇⬇</p>
        </div>
      )}

      <table border="1" cellPadding="5" style={{ borderCollapse: 'collapse', marginTop: '10px' }}>
        <thead>
          <tr>
            <th>적용개시일자</th>
            <th>상한가</th>
            <th>약가변화율</th>
            <th>해설</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => (
            <tr key={idx}>
              <td>{formatDate(row.date)}</td>
              <td>{row.price}</td>
              <td style={{ color: row.change < 0 ? 'red' : row.change > 0 ? 'blue' : 'black' }}>
                {row.change ? `${row.change}%` : ''}
              </td>
              <td>{idx === 0 && metaInfo.설명}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {chartData.length > 0 && (
        <Plot
          data={chartData}
          layout={{
            width: 800,
            height: 400,
            title: '💊 약가 추이',
            xaxis: { title: '적용개시일자' },
            yaxis: { title: '상한가 (원)' }
          }}
        />
      )}
    </div>
  );
}

export default DrugPriceHistory;
