import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement);

const DrugPriceHistory = () => {
  const [data, setData] = useState([]);
  const [ingredientCode, setIngredientCode] = useState('');
  const [filteredData, setFilteredData] = useState([]);
  const [summary, setSummary] = useState(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const workbook = XLSX.read(bstr, { type: 'binary' });
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];
      const rawData = XLSX.utils.sheet_to_json(worksheet, { defval: '' });
      setData(rawData); // ÏóëÏÖÄ Ï†ÑÏ≤¥ Ï†ÄÏû•
    };
    reader.readAsBinaryString(file);
  };

  const handleSearch = () => {
    if (!ingredientCode || data.length === 0) return;

    const filtered = data.filter(row =>
      row &&
      row['Ï£ºÏÑ±Î∂ÑÏΩîÎìú'] &&
      row['Ï£ºÏÑ±Î∂ÑÏΩîÎìú'].toString().includes(ingredientCode)
    );

    const sorted = filtered.sort((a, b) => {
      return b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'];
    });

    const summaryRow = sorted[0] || {};

    // ÏïΩÍ∞Ä Ïù¥Î†• Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
    const priceHistory = [...filtered]
      .sort((a, b) => a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'])
      .map((row, index, arr) => {
        const prev = arr[index - 1];
        const currentPrice = Number(row['ÏÉÅÌïúÍ∞Ä']);
        const prevPrice = prev ? Number(prev['ÏÉÅÌïúÍ∞Ä']) : null;
        const changeRate =
          prevPrice && currentPrice
            ? (((currentPrice - prevPrice) / prevPrice) * 100).toFixed(2)
            : '';
        return {
          date: row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'],
          price: currentPrice,
          changeRate: changeRate ? `${changeRate}%` : '',
        };
      });

    // 2012.04.01 Í∏∞Ï§Ä Ìï¥ÏÑ§Ïö©
    const april2012Row = filtered.find(row => row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] === 20120401);
    let explanation = '';
    if (april2012Row) {
      // 2007.01.01 Ïù¥Ï†Ñ Ï†ÅÏö©Í∞Ä ÌôïÏù∏
      const pre2007 = filtered
        .filter(row => row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] <= 20070101)
        .sort((a, b) => b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'])[0];
      if (pre2007 && pre2007['ÏÉÅÌïúÍ∞Ä']) {
        const before = Number(pre2007['ÏÉÅÌïúÍ∞Ä']);
        const after = Math.round(before * 0.5355);
        explanation = `2007.01.01 Í∏∞Ï§Ä ÏïΩÍ∞Ä ${before}Ïõê ‚Üí 53.55% ÏïΩÍ∞Ä ${after}Ïõê`;
      }
    }

    setFilteredData(filtered);
    setSummary({
      Ï†úÌíàÏΩîÎìú: summaryRow['ÌíàÎ™©ÏΩîÎìú'] || '',
      Ï†úÌíàÎ™Ö: summaryRow['Ï†úÌíàÎ™Ö'] || '',
      ÏóÖÏ≤¥Î™Ö: summaryRow['ÏóÖÏ≤¥Î™Ö'] || '',
      Ï£ºÏÑ±Î∂ÑÏΩîÎìú: ingredientCode,
      ÏïΩÍ∞ÄÏù¥Î†•: priceHistory,
      Ìï¥ÏÑ§: explanation,
    });
  };

  const chartData = {
    labels: summary?.ÏïΩÍ∞ÄÏù¥Î†•.map(p => p.date),
    datasets: [
      {
        label: 'ÏÉÅÌïúÍ∞Ä Ï∂îÏù¥',
        data: summary?.ÏïΩÍ∞ÄÏù¥Î†•.map(p => p.price),
        fill: false,
        borderColor: 'blue',
        tension: 0.1,
      },
    ],
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>üíä ÏïΩÍ∞Ä Ïù¥Î†• Ï°∞Ìöå ÏãúÏä§ÌÖú</h2>
      <p>
        1. Í∏âÏó¨Ïù¥Î†• ÏóëÏÖÄ ÏóÖÎ°úÎìú ‚Üí 2. Ï£ºÏÑ±Î∂ÑÏΩîÎìú ÏûÖÎ†• ‚Üí 3. Í≤∞Í≥º ÌôïÏù∏<br />
        [ÌôïÏù∏ÏÇ¨Ìï≠] 2012.04.01 Ïù∏Ìïò Ïó¨Î∂Ä, 2007ÎÖÑ Í∏∞Ï§ÄÍ∞ÄÏùò 53.55% ÎπÑÍµê Îì±
      </p>
      <input type="file" accept=".xlsx, .xls" onChange={handleFileUpload} />
      <br />
      <input
        type="text"
        placeholder="Ï£ºÏÑ±Î∂ÑÏΩîÎìú ÏûÖÎ†•"
        value={ingredientCode}
        onChange={(e) => setIngredientCode(e.target.value)}
        style={{ marginTop: '10px' }}
      />
      <button onClick={handleSearch} style={{ marginLeft: '10px' }}>
        Ï°∞Ìöå
      </button>

      {summary && (
        <div style={{ marginTop: '30px' }}>
          <p>1. Ï†úÌíàÏΩîÎìú: {summary.Ï†úÌíàÏΩîÎìú}</p>
          <p>2. Ï†úÌíàÎ™Ö: {summary.Ï†úÌíàÎ™Ö}</p>
          <p>3. ÏóÖÏ≤¥Î™Ö: {summary.ÏóÖÏ≤¥Î™Ö}</p>
          <p>4. Ï£ºÏÑ±Î∂ÑÏΩîÎìú: {summary.Ï£ºÏÑ±Î∂ÑÏΩîÎìú}</p>
          <p>5. Í∏âÏó¨Ïù¥Î†• ‚Üì‚Üì‚Üì</p>

          <table border="1" cellPadding="5" style={{ marginTop: '10px' }}>
            <thead>
              <tr>
                <th>Ï†ÅÏö©Í∞úÏãúÏùºÏûê</th>
                <th>ÏÉÅÌïúÍ∞Ä</th>
                <th>ÏïΩÍ∞ÄÎ≥ÄÌôîÏú®</th>
                <th>Ìï¥ÏÑ§</th>
              </tr>
            </thead>
            <tbody>
              {summary.ÏïΩÍ∞ÄÏù¥Î†•.map((row, index) => (
                <tr key={index}>
                  <td>{row.date}</td>
                  <td>{row.price}</td>
                  <td>{row.changeRate}</td>
                  <td>{index === 0 && summary.Ìï¥ÏÑ§}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <div style={{ marginTop: '30px' }}>
            <h3>üìà ÏïΩÍ∞Ä Ï∂îÏù¥</h3>
            <Line data={chartData} />
          </div>
        </div>
      )}
    </div>
  );
};

export default DrugPriceHistory;
