import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

function DrugPriceHistory() {
  const [data, setData] = useState([]);
  const [ingredientCode, setIngredientCode] = useState('');
  const [summary, setSummary] = useState(null);
  const [graphData, setGraphData] = useState(null);

  const handleFileUpload = (e) => {
    const fileReader = new FileReader();
    fileReader.readAsBinaryString(e.target.files[0]);

    fileReader.onload = (e) => {
      const wb = XLSX.read(e.target.result, { type: 'binary' });
      const ws = wb.Sheets[wb.SheetNames[0]];
      const jsonData = XLSX.utils.sheet_to_json(ws);
      setData(jsonData);
    };
  };

  const handleSearch = () => {
    if (!ingredientCode || data.length === 0) return;

    const filtered = data.filter(row => row['Ï£ºÏÑ±Î∂ÑÏΩîÎìú'] === ingredientCode);
    if (filtered.length === 0) return;

    const sorted = [...filtered].sort((a, b) => b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê']);
    const summaryRow = sorted[0];

    const resultRows = [...sorted].map((row, i, arr) => {
      const price = row['ÏÉÅÌïúÍ∞Ä'];
      const prev = arr[i + 1]?.['ÏÉÅÌïúÍ∞Ä'];
      const rate = prev ? (((price - prev) / prev) * 100).toFixed(2) : null;

      const showComment = row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] === 20120401;
      let comment = '';
      if (showComment) {
        const baseRow = arr.find(r => r['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] <= 20070101);
        if (baseRow) {
          const prevPrice = baseRow['ÏÉÅÌïúÍ∞Ä'];
          const converted = Math.round(prevPrice * 0.5355);
          comment = `2007.01.01 Í∏∞Ï§Ä ÏïΩÍ∞Ä ${prevPrice}Ïõê ‚Üí 53.55% ÏïΩÍ∞Ä ${converted}Ïõê`;
        }
      }

      return {
        Ï†ÅÏö©Í∞úÏãúÏùºÏûê: row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'],
        ÏÉÅÌïúÍ∞Ä: price,
        Î≥ÄÌôîÏú®: rate ? `${rate > 0 ? '+' : ''}${rate}%` : '',
        Ìï¥ÏÑ§: comment
      };
    });

    const chartData = {
      labels: resultRows.map(r => `${String(r.Ï†ÅÏö©Í∞úÏãúÏùºÏûê).slice(0, 4)}-${String(r.Ï†ÅÏö©Í∞úÏãúÏùºÏûê).slice(4, 6)}`),
      datasets: [{
        label: 'ÏÉÅÌïúÍ∞Ä (Ïõê)',
        data: resultRows.map(r => r.ÏÉÅÌïúÍ∞Ä),
        fill: false,
        borderColor: 'blue',
        backgroundColor: 'blue',
        tension: 0.2
      }]
    };

    setSummary({
      Ï†úÌíàÏΩîÎìú: summaryRow['ÌíàÎ™©ÏΩîÎìú'],
      Ï†úÌíàÎ™Ö: summaryRow['Ï†úÌíàÎ™Ö'],
      ÏóÖÏ≤¥Î™Ö: summaryRow['ÏóÖÏ≤¥Î™Ö'],
      Ï£ºÏÑ±Î∂ÑÏΩîÎìú: ingredientCode
    });

    setData(resultRows);
    setGraphData(chartData);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>üíä ÏïΩÍ∞Ä Ïù¥Î†• Ï°∞Ìöå ÏãúÏä§ÌÖú</h2>
      <p>1. Í∏âÏó¨Ïù¥Î†• ÏóëÏÖÄ ÌååÏùº ÏóÖÎ°úÎìú ‚Üí 2. Ï£ºÏÑ±Î∂ÑÏΩîÎìú ÏûÖÎ†• ‚Üí 3. Í≤∞Í≥º ÌôïÏù∏</p>
      <p><strong>[Ï§ëÏöî]</strong> 2012ÎÖÑ 4Ïõî 1Ïùº Î≥ÄÍ≤Ω ÏïΩÍ∞ÄÍ∞Ä ÏûàÏùÑ Í≤ΩÏö∞, 2007ÎÖÑ 1Ïõî 1Ïùº Í∏∞Ï§Ä ÏïΩÍ∞ÄÏùò 53.55%Î°ú Í≥ÑÏÇ∞Îêú ÏïΩÍ∞Ä Ìï¥ÏÑ§ÏùÑ Ï†úÍ≥µÌï©ÎãàÎã§.</p>

      <input type="file" onChange={handleFileUpload} />
      <input type="text" value={ingredientCode} onChange={e => setIngredientCode(e.target.value)} placeholder="Ï£ºÏÑ±Î∂ÑÏΩîÎìú ÏûÖÎ†•" />
      <button onClick={handleSearch}>Ï°∞Ìöå</button>

      {summary && (
        <div style={{ marginTop: '20px' }}>
          <p><strong>1. Ï†úÌíàÏΩîÎìú:</strong> {summary.Ï†úÌíàÏΩîÎìú}</p>
          <p><strong>2. Ï†úÌíàÎ™Ö:</strong> {summary.Ï†úÌíàÎ™Ö}</p>
          <p><strong>3. ÏóÖÏ≤¥Î™Ö:</strong> {summary.ÏóÖÏ≤¥Î™Ö}</p>
          <p><strong>4. Ï£ºÏÑ±Î∂ÑÏΩîÎìú:</strong> {summary.Ï£ºÏÑ±Î∂ÑÏΩîÎìú}</p>
          <p><strong>5. Í∏âÏó¨Ïù¥Î†• ‚Üì‚Üì‚Üì</strong></p>
        </div>
      )}

      {data.length > 0 && (
        <table border="1" cellPadding="5" style={{ marginTop: '10px', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Ï†ÅÏö©Í∞úÏãúÏùºÏûê</th>
              <th>ÏÉÅÌïúÍ∞Ä</th>
              <th>ÏïΩÍ∞ÄÎ≥ÄÌôîÏú®</th>
              <th>Ìï¥ÏÑ§</th>
            </tr>
          </thead>
          <tbody>
            {data.map((r, i) => (
              <tr key={i}>
                <td style={{ color: 'red' }}>{formatDate(r.Ï†ÅÏö©Í∞úÏãúÏùºÏûê)}</td>
                <td>{r.ÏÉÅÌïúÍ∞Ä}</td>
                <td style={{ color: r.Î≥ÄÌôîÏú®.includes('-') ? 'red' : 'blue' }}>{r.Î≥ÄÌôîÏú®}</td>
                <td style={{ color: 'red' }}>{r.Ìï¥ÏÑ§}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {graphData && (
        <div style={{ marginTop: '40px' }}>
          <h3>üìà ÏïΩÍ∞Ä Ï∂îÏù¥</h3>
          <Line data={graphData} />
        </div>
      )}
    </div>
  );
}

function formatDate(dateInt) {
  const str = dateInt.toString();
  return `${str.slice(0, 4)}.${str.slice(4, 6)}.${str.slice(6, 8)}`;
}

export default DrugPriceHistory;
