        const baseRow = arr.find(r => r['μ μ©κ°μ‹μΌμ'] <= 20070101);
        if (baseRow) {
          const prevPrice = baseRow['μƒν•κ°€'];
          const converted = Math.round(prevPrice * 0.5355);
          comment = `2007.01.01 κΈ°μ¤€ μ•½κ°€ ${prevPrice}μ› β†’ 53.55% μ•½κ°€ ${converted}μ›`;
        }
