      return {
        μ μ©κ°μ‹μΌμ: row['μ μ©κ°μ‹μΌμ'],
        μƒν•κ°€: price,
        λ³€ν™”μ¨: rate ? `${rate > 0 ? '+' : ''}${rate}%` : '',
        ν•΄μ„¤: comment
