import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

function DrugPriceHistory() {
  const [data, setData] = useState([]);
  const [ingredientCode, setIngredientCode] = useState('');
  const [summary, setSummary] = useState(null);
  const [graphData, setGraphData] = useState(null);

  const handleFileUpload = (e) => {
    const fileReader = new FileReader();
    fileReader.readAsBinaryString(e.target.files[0]);

    fileReader.onload = (e) => {
      const wb = XLSX.read(e.target.result, { type: 'binary' });
      const ws = wb.Sheets[wb.SheetNames[0]];
      const jsonData = XLSX.utils.sheet_to_json(ws);
      setData(jsonData);
    };
  };

  const handleSearch = () => {
    if (!ingredientCode || data.length === 0) return;

    const filtered = data.filter(row => row['주성분코드'] === ingredientCode);
    if (filtered.length === 0) return;

    const sorted = [...filtered].sort((a, b) => b['적용개시일자'] - a['적용개시일자']);
    const summaryRow = sorted[0];

    const resultRows = [...sorted].map((row, i, arr) => {
      const price = row['상한가'];
      const prev = arr[i + 1]?.['상한가'];
      const rate = prev ? (((price - prev) / prev) * 100).toFixed(2) : null;

      const showComment = row['적용개시일자'] === 20120401;
      let comment = '';
      if (showComment) {
        const baseRow = arr.find(r => r['적용개시일자'] <= 20070101);
        if (baseRow) {
          const prevPrice = baseRow['상한가'];
          const converted = Math.round(prevPrice * 0.5355);
          comment = `2007.01.01 기준 약가 ${prevPrice}원 → 53.55% 약가 ${converted}원`;
        }
      }

      return {
        적용개시일자: row['적용개시일자'],
        상한가: price,
        변화율: rate ? `${rate > 0 ? '+' : ''}${rate}%` : '',
        해설: comment
      };
    });

    const chartData = {
      labels: resultRows.map(r => `${String(r.적용개시일자).slice(0, 4)}-${String(r.적용개시일자).slice(4, 6)}`),
      datasets: [{
        label: '상한가 (원)',
        data: resultRows.map(r => r.상한가),
        fill: false,
        borderColor: 'blue',
        backgroundColor: 'blue',
        tension: 0.2
      }]
    };

    setSummary({
      제품코드: summaryRow['품목코드'],
      제품명: summaryRow['제품명'],
      업체명: summaryRow['업체명'],
      주성분코드: ingredientCode
    });

    setData(resultRows);
    setGraphData(chartData);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>💊 약가 이력 조회 시스템</h2>

      <div style={{ marginBottom: '20px', background: '#f9f9f9', padding: '15px', border: '1px solid #ddd' }}>
        <p><strong>특정품목의 약가이력을 찾습니다.</strong></p>
        <p>1) 심평원 약가이력 엑셀 다운로드 ▶ 2) 해당파일 업로드 ▶ 3) 엑셀파일내 찾고싶은 품목 주성분코드 입력 ▶ 4) 결과확인</p>
        <p><strong>[주요확인 내용]</strong></p>
        <ul>
          <li>복합제 산정시 기준약가</li>
          <li>2012년 4월 1일 일괄인하 여부 (2007년 1월 1일 기준 53.55% 적용 약가 여부)</li>
          <li>재평가로 20% 인하 적용 여부 (3년에 걸쳐 7%-7%-6%)</li>
          <li>사용량-약가 연동협상으로 인한 약가인하</li>
          <li>실거래가 약가인하</li>
        </ul>
        <p><strong>[심평원 의약품통합정보 ‘급여 이력정보 검색‘ 링크]</strong></p>
        <a href="https://www.hira.or.kr/ra/medi/getHistoryList.do?pgmid=HIRAA030035020000" target="_blank" rel="noreferrer">
          https://www.hira.or.kr/ra/medi/getHistoryList.do?pgmid=HIRAA030035020000
        </a>
      </div>

      <input type="file" onChange={handleFileUpload} />
      <input type="text" value={ingredientCode} onChange={e => setIngredientCode(e.target.value)} placeholder="주성분코드 입력" />
      <button onClick={handleSearch}>조회</button>

      {summary && (
        <div style={{ marginTop: '20px' }}>
          <p><strong>1. 제품코드:</strong> {summary.제품코드}</p>
          <p><strong>2. 제품명:</strong> {summary.제품명}</p>
          <p><strong>3. 업체명:</strong> {summary.업체명}</p>
          <p><strong>4. 주성분코드:</strong> {summary.주성분코드}</p>
          <p><strong>5. 급여이력 ↓↓↓</strong></p>
        </div>
      )}

      {data.length > 0 && (
        <table border="1" cellPadding="5" style={{ marginTop: '10px', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>적용개시일자</th>
              <th>상한가</th>
              <th>약가변화율</th>
              <th>해설</th>
            </tr>
          </thead>
          <tbody>
            {data.map((r, i) => (
              <tr key={i}>
                <td style={{ color: 'red' }}>{formatDate(r.적용개시일자)}</td>
                <td>{r.상한가}</td>
                <td style={{ color: r.변화율.includes('-') ? 'red' : 'blue' }}>{r.변화율}</td>
                <td style={{ color: 'red' }}>{r.해설}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {graphData && (
        <div style={{ marginTop: '40px' }}>
          <h3>📈 약가 추이</h3>
          <Line data={graphData} />
        </div>
      )}
    </div>
  );
}

function formatDate(dateInt) {
  const str = dateInt.toString();
  return `${str.slice(0, 4)}.${str.slice(4, 6)}.${str.slice(6, 8)}`;
}

export default DrugPriceHistory;
