import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement);

const DrugPriceHistory = () => {
  const [data, setData] = useState([]);
  const [ingredientCode, setIngredientCode] = useState('');
  const [summary, setSummary] = useState(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const workbook = XLSX.read(bstr, { type: 'binary' });
      const worksheet = workbook.Sheets[workbook.SheetNames[0]];
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { defval: '' });
      setData(jsonData);
    };
    reader.readAsBinaryString(file);
  };

  const handleSearch = () => {
    if (!ingredientCode || data.length === 0) return;

    const filtered = data.filter(row =>
      row &&
      row['Ï£ºÏÑ±Î∂ÑÏΩîÎìú'] &&
      row['Ï£ºÏÑ±Î∂ÑÏΩîÎìú'].toString().includes(ingredientCode)
    );

    const sorted = [...filtered].sort((a, b) => b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê']);
    const summaryRow = sorted[0] || {};

    // ÏïΩÍ∞Ä Ïù¥Î†• Ï†ïÎ†¨: Ïò§ÎûòÎêú ÎÇ†Ïßú ‚Üí ÏµúÏã† ÎÇ†Ïßú
    const history = [...filtered]
      .sort((a, b) => a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'])
      .map((row, index, arr) => {
        const prev = arr[index - 1];
        const currentPrice = Number(row['ÏÉÅÌïúÍ∞Ä']);
        const prevPrice = prev ? Number(prev['ÏÉÅÌïúÍ∞Ä']) : null;
        let rate = '';
        if (prevPrice) {
          const change = ((currentPrice - prevPrice) / prevPrice) * 100;
          rate = `${change.toFixed(2)}%`;
        }
        return {
          date: row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'],
          price: currentPrice,
          rate,
        };
      });

    // Ìï¥ÏÑ§ ÏÉùÏÑ±
    let explanation = '';
    const hasApril2012 = filtered.some(row => row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] === 20120401);
    if (hasApril2012) {
      const base = filtered
        .filter(row => row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] <= 20070101)
        .sort((a, b) => b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'])[0];
      if (base && base['ÏÉÅÌïúÍ∞Ä']) {
        const before = Number(base['ÏÉÅÌïúÍ∞Ä']);
        const after = Math.round(before * 0.5355);
        explanation = `2007.01.01 Í∏∞Ï§Ä ÏïΩÍ∞Ä ${before}Ïõê ‚Üí 53.55% ÏïΩÍ∞Ä ${after}Ïõê`;
      }
    }

    setSummary({
      Ï†úÌíàÏΩîÎìú: summaryRow['ÌíàÎ™©ÏΩîÎìú'] || '',
      Ï†úÌíàÎ™Ö: summaryRow['Ï†úÌíàÎ™Ö'] || '',
      ÏóÖÏ≤¥Î™Ö: summaryRow['ÏóÖÏ≤¥Î™Ö'] || '',
      Ï£ºÏÑ±Î∂ÑÏΩîÎìú: ingredientCode,
      ÏïΩÍ∞ÄÏù¥Î†•: history.reverse(), // ÏµúÍ∑º ‚Üí Í≥ºÍ±∞Î°ú Ïó≠Ïàú
      Ìï¥ÏÑ§: explanation,
    });
  };

  const chartData = {
    labels: summary?.ÏïΩÍ∞ÄÏù¥Î†•.map(p => p.date),
    datasets: [
      {
        label: 'ÏÉÅÌïúÍ∞Ä Ï∂îÏù¥',
        data: summary?.ÏïΩÍ∞ÄÏù¥Î†•.map(p => p.price),
        fill: false,
        borderColor: 'blue',
        tension: 0.1,
      },
    ],
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>üíä ÏïΩÍ∞Ä Ïù¥Î†• Ï°∞Ìöå ÏãúÏä§ÌÖú</h2>
      <div style={{ marginBottom: '20px' }}>
        <p>ÌäπÏ†ïÌíàÎ™©Ïùò ÏïΩÍ∞ÄÏù¥Î†•ÏùÑ Ï∞æÏäµÎãàÎã§.</p>
        <p>[Ï†àÏ∞®]<br />
          1) Ïã¨ÌèâÏõê ÏïΩÍ∞ÄÏù¥Î†• ÏóëÏÖÄ Îã§Ïö¥Î°úÎìú ‚ñ∂ 2) Ìï¥ÎãπÌååÏùº ÏóÖÎ°úÎìú ‚ñ∂ 3) ÏóëÏÖÄÌååÏùºÎÇ¥ Ï∞æÍ≥†Ïã∂ÏùÄ ÌíàÎ™© Ï£ºÏÑ±Î∂ÑÏΩîÎìú ÏûÖÎ†• ‚ñ∂ 4) Í≤∞Í≥ºÌôïÏù∏
        </p>
        <p>[Ï£ºÏöîÌôïÏù∏ ÎÇ¥Ïö©]</p>
        <ol>
          <li>Î≥µÌï©Ï†ú ÏÇ∞Ï†ïÏãú Í∏∞Ï§ÄÏïΩÍ∞Ä</li>
          <li>2012ÎÖÑ 4Ïõî 1Ïùº ÏùºÍ¥ÑÏù∏Ìïò Ïó¨Î∂Ä (2007ÎÖÑ 1Ïõî 1Ïùº Í∏∞Ï§Ä 53.55% Ï†ÅÏö© ÏïΩÍ∞ÄÏù∏ÏßÄ Ïó¨Î∂Ä)</li>
          <li>Ïû¨ÌèâÍ∞ÄÎ°ú 20% Ïù∏Ìïò Ï†ÅÏö©Ïó¨Î∂Ä (3ÎÖÑÏóê Í±∏Ï≥ê 7%-7%-6% Ïù∏Ìïò Ï†ÅÏö© ÌôïÏù∏)</li>
          <li>ÏÇ¨Ïö©Îüâ-ÏïΩÍ∞Ä Ïó∞ÎèôÌòëÏÉÅÏúºÎ°ú Ïù∏Ìïú ÏïΩÍ∞ÄÏù∏Ìïò ÌôïÏù∏</li>
          <li>Ïã§Í±∞ÎûòÍ∞Ä ÏïΩÍ∞ÄÏù∏Ìïò ÌôïÏù∏</li>
        </ol>
