import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement);

const DrugPriceHistory = () => {
  const [data, setData] = useState([]);
  const [ingredientCode, setIngredientCode] = useState('');
  const [summary, setSummary] = useState(null);

  const formatDate = (yyyymmdd) => {
    const s = yyyymmdd.toString();
    return `${s.slice(0, 4)}-${s.slice(4, 6)}-${s.slice(6, 8)}`;
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const workbook = XLSX.read(bstr, { type: 'binary' });
      const worksheet = workbook.Sheets[workbook.SheetNames[0]];
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { defval: '' });
      setData(jsonData);
    };
    reader.readAsBinaryString(file);
  };

  const handleSearch = () => {
    if (!ingredientCode || data.length === 0) return;

    let filtered = data.filter(row =>
      row?.['Ï£ºÏÑ±Î∂ÑÏΩîÎìú']?.toString().includes(ingredientCode)
    );

    // Ï§ëÎ≥µ Ï†úÍ±∞ (ÎèôÏùº ÎÇ†Ïßú Ï†úÍ±∞)
    const seenDates = new Set();
    filtered = filtered.filter(row => {
      const date = row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'];
      if (seenDates.has(date)) return false;
      seenDates.add(date);
      return true;
    });

    // Í∞ôÏùÄ Í∞ÄÍ≤© Ï§ë Í∞ÄÏû• Îπ†Î•∏ ÎÇ†ÏßúÎßå ÎÇ®Í∏∞Í∏∞
    const priceMap = new Map();
    for (let row of filtered) {
      const price = row['ÏÉÅÌïúÍ∞Ä'];
      const date = row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'];
      if (!priceMap.has(price) || priceMap.get(price) > date) {
        priceMap.set(price, date);
      }
    }
    filtered = filtered.filter(row => priceMap.get(row['ÏÉÅÌïúÍ∞Ä']) === row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê']);

    // ÏÉÅÌïúÍ∞ÄÍ∞Ä 0Ïù∏ Ìñâ Ï†úÍ±∞
    filtered = filtered.filter(row => row['ÏÉÅÌïúÍ∞Ä'] !== 0);

    // ÎÇ†Ïßú Ï†ïÎ†¨ Ïò§Î¶ÑÏ∞®Ïàú (Î≥ÄÌôîÏú® Í≥ÑÏÇ∞Ïö©)
    const sorted = [...filtered].sort((a, b) => a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê']);

    const history = sorted.map((row, index) => {
      const currentPrice = Number(row['ÏÉÅÌïúÍ∞Ä']);
      const currentDate = row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'];
      let prev = null;

      for (let i = index - 1; i >= 0; i--) {
        if (sorted[i]['ÏÉÅÌïúÍ∞Ä'] !== 0) {
          prev = sorted[i];
          break;
        }
      }

      const prevPrice = prev ? Number(prev['ÏÉÅÌïúÍ∞Ä']) : null;
      let rate = '';
      if (prevPrice) {
        const change = ((currentPrice - prevPrice) / prevPrice) * 100;
        rate = `${change > 0 ? '+' : ''}${change.toFixed(2)}%`;
      }

      return {
        date: currentDate,
        price: currentPrice,
        rate,
      };
    });

    const summaryRow = sorted[sorted.length - 1] || {};

    // Ìï¥ÏÑ§
    let explanation = '';
    const hasApril2012 = sorted.some(row => row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] === 20120401);
    if (hasApril2012) {
      const base = sorted
        .filter(row => row['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] <= 20070101)
        .sort((a, b) => b['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'] - a['Ï†ÅÏö©Í∞úÏãúÏùºÏûê'])[0];
      if (base && base['ÏÉÅÌïúÍ∞Ä']) {
        const before = Number(base['ÏÉÅÌïúÍ∞Ä']);
        const after = Math.round(before * 0.5355);
        explanation = `2007.01.01 Í∏∞Ï§Ä ÏïΩÍ∞Ä ${before}Ïõê ‚Üí 53.55% ÏïΩÍ∞Ä ${after}Ïõê`;
