import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement);

const DrugPriceHistory = () => {
  const [data, setData] = useState([]);
  const [ingredientCode, setIngredientCode] = useState('');
  const [summary, setSummary] = useState(null);

  const formatDate = (yyyymmdd) => {
    const s = yyyymmdd.toString();
    return `${s.slice(0, 4)}-${s.slice(4, 6)}-${s.slice(6, 8)}`;
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const workbook = XLSX.read(bstr, { type: 'binary' });
      const worksheet = workbook.Sheets[workbook.SheetNames[0]];
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { defval: '' });
      setData(jsonData);
    };
    reader.readAsBinaryString(file);
  };

  const handleSearch = () => {
    if (!ingredientCode || data.length === 0) return;

    let filtered = data.filter(row =>
      row?.['주성분코드']?.toString().includes(ingredientCode)
    );

    // 중복 제거 (동일 날짜 제거)
    const seenDates = new Set();
    filtered = filtered.filter(row => {
      const date = row['적용개시일자'];
      if (seenDates.has(date)) return false;
      seenDates.add(date);
      return true;
    });

    // 같은 가격 중 가장 빠른 날짜만 남기기
    const priceMap = new Map();
    for (let row of filtered) {
      const price = row['상한가'];
      const date = row['적용개시일자'];
      if (!priceMap.has(price) || priceMap.get(price) > date) {
        priceMap.set(price, date);
      }
    }
    filtered = filtered.filter(row => priceMap.get(row['상한가']) === row['적용개시일자']);

    // 상한가가 0인 행 제거
    filtered = filtered.filter(row => row['상한가'] !== 0);

    // 날짜 정렬 오름차순 (변화율 계산용)
    const sorted = [...filtered].sort((a, b) => a['적용개시일자'] - b['적용개시일자']);

    const history = sorted.map((row, index) => {
      const currentPrice = Number(row['상한가']);
      const currentDate = row['적용개시일자'];
      let prev = null;

      for (let i = index - 1; i >= 0; i--) {
        if (sorted[i]['상한가'] !== 0) {
          prev = sorted[i];
          break;
        }
      }

      const prevPrice = prev ? Number(prev['상한가']) : null;
      let rate = '';
      if (prevPrice) {
        const change = ((currentPrice - prevPrice) / prevPrice) * 100;
        rate = `${change > 0 ? '+' : ''}${change.toFixed(2)}%`;
      }

      return {
        date: currentDate,
        price: currentPrice,
        rate,
      };
    });

    const summaryRow = sorted[sorted.length - 1] || {};

    // 해설
    let explanation = '';
    const hasApril2012 = sorted.some(row => row['적용개시일자'] === 20120401);
    if (hasApril2012) {
      const base = sorted
        .filter(row => row['적용개시일자'] <= 20070101)
        .sort((a, b) => b['적용개시일자'] - a['적용개시일자'])[0];
      if (base && base['상한가']) {
        const before = Number(base['상한가']);
        const after = Math.round(before * 0.5355);
        explanation = `2007.01.01 기준 약가 ${before}원 → 53.55% 약가 ${after}원`;
      }
    }

    // 내림차순 정렬 (최신 날짜 먼저 보여주기)
    const finalHistory = [...history].sort((a, b) => b.date - a.date);

    setSummary({
      제품코드: summaryRow['품목코드'] || '',
      제품명: summaryRow['제품명'] || '',
      업체명: summaryRow['업체명'] || '',
      주성분코드: ingredientCode,
      약가이력: finalHistory,
      해설: explanation,
    });
  };

  const chartData = {
    labels: summary?.약가이력.map(p => formatDate(p.date)),
    datasets: [
      {
        label: '상한가 추이',
        data: summary?.약가이력.map(p => p.price),
        fill: false,
        borderColor: 'blue',
        tension: 0.1,
      },
    ],
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>💊 약가 이력 조회 시스템</h2>
      <div style={{ marginBottom: '20px' }}>
        <p>특정품목의 약가이력을 찾습니다.</p>
        <p>[절차]<br />
          1) 심평원 약가이력 엑셀 다운로드 ▶ 2) 해당파일 업로드 ▶ 3) 엑셀파일내 찾고싶은 품목 주성분코드 입력 ▶ 4) 결과확인
        </p>
        <p>[주요확인 내용]</p>
        <ol>
          <li>복합제 산정시 기준약가</li>
          <li>2012년 4월 1일 일괄인하 여부 (2007년 1월 1일 기준 53.55% 적용 약가인지 여부)</li>
          <li>재평가로 20% 인하 적용여부 (3년에 걸쳐 7%-7%-6% 인하 적용 확인)</li>
          <li>사용량-약가 연동협상으로 인한 약가인하 확인</li>
          <li>실거래가 약가인하 확인</li>
        </ol>
        <p>
          🔗 <a
            href="https://www.hira.or.kr/ra/medi/getHistoryList.do?pgmid=HIRAA030035020000&WT.gnb=%EC%9D%98%EC%95%BD%ED%92%88%ED%86%B5%ED%95%A9%EC%A0%95%EB%B3%B4"
            target="_blank" rel="noreferrer">
            심평원 의약품통합정보 ‘급여 이력정보 검색’ 링크
          </a>
        </p>
      </div>

      <input type="file" accept=".xlsx, .xls" onChange={handleFileUpload} />
      <br />
      <input
        type="text"
        placeholder="주성분코드 입력"
        value={ingredientCode}
        onChange={(e) => setIngredientCode(e.target.value)}
        style={{ marginTop: '10px' }}
      />
      <button onClick={handleSearch} style={{ marginLeft: '10px' }}>
        조회
      </button>

      {summary && (
        <div style={{ marginTop: '30px' }}>
          <p>1. 제품코드: {summary.제품코드}</p>
          <p>2. 제품명: {summary.제품명}</p>
          <p>3. 업체명: {summary.업체명}</p>
          <p>4. 주성분코드: {summary.주성분코드}</p>
          <p>5. 급여이력 ↓↓↓</p>

          <table border="1" cellPadding="5" style={{ marginTop: '10px', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>적용개시일자</th>
                <th>상한가</th>
                <th>약가변화율</th>
                <th>해설</th>
              </tr>
            </thead>
            <tbody>
              {summary.약가이력.map((row, index) => (
                <tr key={index}>
                  <td>{formatDate(row.date)}</td>
                  <td>{row.price}</td>
                  <td style={{ color: row.rate.startsWith('-') ? 'red' : row.rate.startsWith('+') ? 'blue' : 'black' }}>
                    {row.rate}
                  </td>
                  <td>{index === 0 && summary.해설}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <div style={{ marginTop: '30px' }}>
            <h3>📈 약가 추이</h3>
            <Line data={chartData} />
          </div>
        </div>
      )}
    </div>
  );
};

export default DrugPriceHistory;
