import React, { useState } from "react";
import * as XLSX from "xlsx";
import Plot from "react-plotly.js";
import { Link } from "react-router-dom";

function DrugPriceHistory() {
  const [excelData, setExcelData] = useState([]);
  const [code, setCode] = useState("");
  const [result, setResult] = useState(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const data = XLSX.utils.sheet_to_json(ws, { defval: "" });
      setExcelData(data);
    };

    reader.readAsBinaryString(file);
  };

  const formatDate = (yyyymmdd) => {
    const str = yyyymmdd.toString();
    if (str.length !== 8) return str;
    return `${str.slice(0, 4)}-${str.slice(4, 6)}-${str.slice(6, 8)}`;
  };

  const calculateChangeRate = (oldPrice, newPrice) => {
    if (oldPrice === 0) return "0.00%";
    const rate = ((newPrice - oldPrice) / oldPrice) * 100;
    return `${rate > 0 ? "+" : ""}${rate.toFixed(2)}%`;
  };

  const handleSearch = () => {
    if (!code || excelData.length === 0) return;

    const filtered = excelData
      .filter(
        (row) =>
          row.주성분코드 === code &&
          typeof row.상한금액 === "number" &&
          row.상한금액 > 0
      )
      .sort((a, b) => a.적용개시일자 - b.적용개시일자);

    const groupedByDate = {};
    filtered.forEach((row) => {
      const date = row.적용개시일자;
      if (!groupedByDate[date]) groupedByDate[date] = row;
    });

    const uniqueRows = Object.values(groupedByDate);

    const finalFiltered = [];
    let prevPrice = null;
    for (let i = 0; i < uniqueRows.length; i++) {
      const row = uniqueRows[i];
      if (row.상한금액 !== prevPrice) {
        finalFiltered.push(row);
        prevPrice = row.상한금액;
      }
    }

    const resultRows = finalFiltered
      .map((row, idx) => {
        const currentPrice = row.상한금액;
        const prevRow = finalFiltered[idx - 1];
        let changeRate = "-";
        if (prevRow) {
          const prevPrice = prevRow.상한금액;
          changeRate = calculateChangeRate(prevPrice, currentPrice);
        }

        let comment = "";

        // 2012년 4월 1일 기준 해설
        if (row.적용개시일자 === 20120401) {
          const 기준일 = 20070101;
          const 기준약가행 = [...finalFiltered]
            .filter((r) => r.적용개시일자 < 기준일)
            .sort((a, b) => b.적용개시일자 - a.적용개시일자)[0];

          if (기준약가행) {
            const 기준가 = 기준약가행.상한금액;
            const 적용가 = Math.round((기준가 * 53.55) / 100);
            comment = `2007-01-01 기준 약가 ${기준가}원 → 53.55% 약가 ${적용가}원`;
          }
        }

        return {
          적용개시일자: formatDate(row.적용개시일자),
          상한가: row.상한금액,
          약가변화율: changeRate,
          해설: comment,
        };
      })
      .reverse();

    const latestRow = [...finalFiltered].sort(
      (a, b) => b.적용개시일자 - a.적용개시일자
    )[0];

    setResult({
      코드: code,
      제품코드: latestRow.품목기준코드,
      제품명: latestRow.제품명,
      업체명: latestRow.업소명,
      이력: resultRows,
      그래프: finalFiltered.map((row) => ({
        날짜: formatDate(row.적용개시일자),
        금액: row.상한금액,
      })),
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>
        <Link to="/" style={{ textDecoration: "none", color: "black" }}>
          💊 MarketAccessNow 홈으로
        </Link>
      </h2>

      <h3>📌 약가 이력 조회 시스템</h3>
      <p>특정품목의 약가이력을 찾습니다.</p>
      <p>
        <strong>[절차]</strong>
        <br />
        1) 심평원 약가이력 엑셀 다운로드 ▶ 2) 해당파일 업로드 ▶ 3) 엑셀파일내
        찾고싶은 품목 주성분코드 입력 ▶ 4) 결과확인
      </p>
      <p>
        <strong>[주요확인 내용]</strong>
        <br />
        1) 복합제 산정시 기준약가
        <br />
        2) 2012년 4월 1일 일괄인하 여부 (2007년 1월 1일 기준 53.55% 적용
        약가인지 여부)
        <br />
        3) 재평가로 20% 인하 적용여부 (3년에 걸쳐 7%-7%-6% 인하 적용 확인)
        <br />
        4) 사용량-약가 연동협상으로 인한 약가인하 확인
        <br />
        5) 실거래가 약가인하 확인
      </p>
      <p>
        🔗{" "}
        <a
          href="https://www.hira.or.kr/ra/medi/getHistoryList.do?pgmid=HIRAA030035020000&WT.gnb=%EC%9D%98%EC%95%BD%ED%92%88%ED%86%B5%ED%95%A9%EC%A0%95%EB%B3%B4"
          target="_blank"
          rel="noopener noreferrer"
          style={{ color: "blue", textDecoration: "underline" }}
        >
          심평원 의약품통합정보 ‘급여 이력정보 검색’ 링크 바로가기
        </a>
      </p>

      <div style={{ marginTop: "20px" }}>
        <input type="file" onChange={handleFileUpload} />
      </div>
      <div style={{ marginTop: "10px" }}>
        <input
          type="text"
          placeholder="주성분코드 입력"
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />
        <button onClick={handleSearch} style={{ marginLeft: "10px" }}>
          조회
        </button>
      </div>

      {result && (
        <div style={{ marginTop: "30px" }}>
          <p>1. 제품코드: {result.제품코드}</p>
          <p>2. 제품명: {result.제품명}</p>
          <p>3. 업체명: {result.업체명}</p>
          <p>4. 주성분코드: {result.코드}</p>
          <p>5. 급여이력 ⬇⬇⬇</p>

          <table border="0" cellPadding="8">
            <thead>
              <tr>
                <th>적용개시일자</th>
                <th>상한가</th>
                <th>약가변화율</th>
                <th>해설</th>
              </tr>
            </thead>
            <tbody>
              {result.이력.map((row, i) => (
                <tr key={i}>
                  <td>{row.적용개시일자}</td>
                  <td>{row.상한가}</td>
                  <td
                    style={{
                      color: row.약가변화율.startsWith("+")
                        ? "blue"
                        : row.약가변화율.startsWith("-")
                        ? "red"
                        : "black",
                    }}
                  >
                    {row.약가변화율}
                  </td>
                  <td style={{ color: "red" }}>{row.해설}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <h4 style={{ marginTop: "40px" }}>📈 약가 추이</h4>
          <Plot
            data={[
              {
                x: result.그래프.map((d) => d.날짜),
                y: result.그래프.map((d) => d.금액),
                type: "scatter",
                mode: "lines+markers",
                marker: { color: "blue" },
              },
            ]}
            layout={{
              width: 700,
              height: 400,
              title: "💊 약가 추이",
              xaxis: { title: "적용 개시일자" },
              yaxis: { title: "상한가 (원)" },
            }}
          />
        </div>
      )}
    </div>
  );
}

export default DrugPriceHistory;
