import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from 'chart.js';
import { useNavigate } from 'react-router-dom';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

function DrugPriceHistory() {
  const [result, setResult] = useState(null);
  const [code, setCode] = useState('');
  const [file, setFile] = useState(null);
  const navigate = useNavigate();

  const parseDate = (dateStr) => {
    if (!dateStr || dateStr.length !== 8) return '';
    return `${dateStr.slice(0, 4)}-${dateStr.slice(4, 6)}-${dateStr.slice(6)}`;
  };

  const handleFile = async (e) => {
    const f = e.target.files[0];
    setFile(f);
  };

  const calculateChangeRate = (oldPrice, newPrice) => {
    if (!oldPrice || oldPrice === 0) return null;
    return (((newPrice - oldPrice) / oldPrice) * 100).toFixed(2);
  };

  const formatCurrency = (val) => Number(val).toLocaleString();

  const handleProcess = async () => {
    if (!file || !code) return;

    const data = await file.arrayBuffer();
    const workbook = XLSX.read(data);
    const sheet = workbook.Sheets[workbook.SheetNames[0]];
    const json = XLSX.utils.sheet_to_json(sheet);

    let filtered = json.filter(row => row.Ï£ºÏÑ±Î∂ÑÏΩîÎìú?.toString().includes(code) && row.ÏÉÅÌïúÍ∞Ä !== 0);

    // ÎèôÏùºÌïú ÏÉÅÌïúÍ∞ÄÎ©¥ Í∞ÄÏû• Îπ†Î•∏ ÎÇ†ÏßúÎßå ÎÇ®ÍπÄ
    const deduped = [];
    const seenDates = new Set();
    filtered.forEach(row => {
      const date = row.Ï†ÅÏö©Í∞úÏãúÏùºÏûê;
      const price = row.ÏÉÅÌïúÍ∞Ä;
